package sec03;

public class MyCircle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int radius = 10;
		double area;
		final double PI = 3.141592;
		
	    System.out.println("radius = " + radius);
	    area = radius * radius * PI;
		System.out.println("area = " + area) ;
	}

}
